#include <iostream>#include <vector>#include <cstdio>#include <algorithm>using namespace std;typedef pair<int, char> ii;bool cmp(ii a, ii b){	if(a.first != b.first){		return a.first > b.first;	}	else{		return a.second < b.second;	}}int main() {	char a;	vector<ii> apl;	for(int i = 0; i < 26; i++){		apl.push_back(ii(0, 'a'+i));	}	cin >> a;	while(cin >> a){		if(isalpha(a)){			apl[tolower(a)-'a'].first++;		}	}	sort(apl.begin(), apl.end(), cmp);	for(int i = 0; i < 26 && apl[i].first; i++){		cout << char(toupper(apl[i].second)) << ' ' << apl[i].first << endl;	}	return 0;}