#include <iostream>#include <set>using namespace std;typedef pair<int, int> ii;int main(){	set<ii> s;	set<ii>::iterator it;	s.insert(ii(-1, -1));	it = s.begin();	int n, idx = 0, ans;	while(cin >> n){		s.insert(ii(n, idx++));		if(n >= it->first && idx%2 == 1){			it++;		}		else if(n < it->first && idx%2 == 0){			it--;		}		//cout << it->first << ' ' << n << endl;		if(idx%2 == 1){			cout << (it->first) << endl;		}		else{			ans = (++it)->first;			ans += (--it)->first;			cout << ans/2 << endl;		}	}		return 0;}