#include <iostream>using namespace std;int main(){	string x, hex;	long long dec, idx;	while(cin >> x && x[0] != '-'){		if(x.size() > 1 && x[1] == 'x'){			dec = 0; idx = 1;			for(int i = x.size()-1; i > 1; i--){				if(isalpha(x[i])){					dec += idx*(10+x[i]-'A');				}				else{					dec += idx*(x[i]-'0');				}				idx *= 16;			}			cout << dec << endl;		}		else if(x == "0"){			cout << "0x0" << endl;		}		else{			dec = 0;			hex = "";			for(int i = 0; i < x.size(); i++){				dec *= 10;				dec += x[i]-'0';			}			while(dec > 0){				if(dec%16 >= 10){					hex += (char)((dec%16-10)+'A');				}				else{					hex += (char)(dec%16 + '0');				}				dec /= 16;			}			cout << "0x";			for(int i = hex.size()-1; i >= 0; i--){				cout << hex[i];			}			cout << endl;		}	}			return 0;}