#include <iostream>#include <vector>#include <algorithm>using namespace std;int main(){	int n;	cin >> n;	string a;	vector<int> ans;	char apl[70];	for(int i = 0; i < 32; i++){		apl[(i)*2] = ('A'+i);		apl[(i)*2+1] = ('a'+i);	}	for(int i = 0; i < n; i++){		cin >> a;		ans.clear();		for(int j = 0; j < a.size(); j++){			if(isupper(a[j])){				ans.push_back((a[j]-'A')*2);			}			else{				ans.push_back((a[j]-'a')*2+1);			}		}		sort(ans.begin(), ans.end());		do{			for(int j = 0; j < ans.size(); j++){				cout << apl[ans[j]];			}			cout << endl;		}while(next_permutation(ans.begin(), ans.end()));	}		return 0;}